import { Component } from 'react';
import './App.css';
import Logo from './components/Logo';
import Search from './components/Search';
import Card from './components/Card';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      location: '',
      weather: '',
      typing: '',
      api: '04a4336902f247d8cb2f967b426f2481',
    };
  }
  input = (event) => {
    this.setState({ typing: event.target.value });
    console.log('this is the input', event.target.value);
  };
  press = () => {
    this.setState({ location: this.state.typing });

    fetch(
      `http://api.openweathermap.org/data/2.5/forecast?id=524901&appid=04a4336902f247d8cb2f967b426f2481&city name=${this.state.typing}`
    )
      .then((res) => res.json())
      .then((city) => this.setState({ weather: city }));

    console.log('this is the location', this.state.typing);
  };

  render() {
    return (
      <div className="App">
        <Logo />
        <Search className="ma3" input={this.input} press={this.press} />
        <Card weather={this.state.weather} />
      </div>
    );
  }
}

export default App;
