import { Component } from 'react';
import './App.css';
import Logo from './components/Logo';
import Search from './components/Search';
import Card from './components/Card';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      location: '',
      weather: '',
      typing: '',
    };
  }
  input = (event) => {
    this.setState({ typing: event.target.value });
    console.log('this is the input', event.target.value);
  };
  press = () => {
    this.setState({ location: this.state.typing });

    console.log('this is the location', this.state.typing);
  };

  render() {
    return (
      <div className="App">
        <Logo />
        <Search className="pa" input={this.input} press={this.press} />
        <Card />
      </div>
    );
  }
}

export default App;
