import { Component } from 'react';
import './App.css';
import Logo from './components/Logo';
import Search from './components/Search';
import Card from './components/Card';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      location: '',
      weather: '',
      typing: '',
      api: '04a4336902f247d8cb2f967b426f2481',
    };
  }
  input = (event) => {
    this.setState({ typing: event.target.value });
    console.log('this is the input', event.target.value);
  };
  press = () => {
    this.setState({ location: this.state.typing });

    fetch('URL_GOES_HERE', {
      method: 'post',
      headers: new Headers({
        Authorization: 'Basic ' + btoa('username:password'),
        'Content-Type': 'application/x-www-form-urlencoded',
      }),
      body: 'A=1&B=2',
    });

    console.log('this is the location', this.state.typing);
  };

  render() {
    return (
      <div className="App">
        <Logo />
        <Search className="ma3" input={this.input} press={this.press} />
        <Card />
      </div>
    );
  }
}

export default App;
